#include <iostream>
#include <vector>

void bubbleSort(std::vector<int>& arr) {
    int n = arr.size();
    for (int i = 0; i < n - 1; i++) {
        for (int j = 0; j < n - i - 1; j++) {
            if (arr[j] > arr[j + 1]) {
                std::swap(arr[j], arr[j + 1]);
            }
        }
    }
}

int main() {
    std::vector<int> numbers = {5, 2, 8, 1, 9, 4};
    bubbleSort(numbers);
    for (int num : numbers) {
        std::cout << num << " "; // Output: 1 2 4 5 8 9
    }
    std::cout << std::endl;
    return 0;
}
